const emailVerificar=async(email,html)=>{
    try {
        await transporter.sendMail({
            from: mail.user, // sender address
            to: email, // list of receivers
            subject: "Validación de cuenta", // Subject line
            html: html, // html body
        });
        
    } catch (error) {
        console.log(error)
    }
}
